import React, { useContext, useEffect } from 'react';
import ExpenseItem from "./ExpenseItem";
import { AppContext } from "../../context/AppContext";
import { Expense } from "../../types/types";
import { fetchExpenses, deleteExpense } from "../../utils/expense-utils"; // deleteExpense 함수 추가

const ExpenseList = () => {
  const { expenses, setExpenses } = useContext(AppContext); 

  useEffect(() => {
    loadExpenses();
  }, []);

  const loadExpenses = async () => {
    try {
      const expenseList = await fetchExpenses();
      setExpenses(expenseList);
    } catch (err: any) {
      console.error("Error loading expenses:", err.message);
    }
  };

  // 서버에 삭제 요청을 보내는 함수
  const handleDelete = async (id: string) => {
    try {
      await deleteExpense(id); // 서버에 삭제 요청
      setExpenses(expenses.filter((expense) => expense.id !== id)); // UI에서 항목 삭제
    } catch (err: any) {
      console.error("Failed to delete expense:", err.message);
    }
  };

  return (
    <ul className="list-group">
      {expenses.map((expense: Expense) => (
        <ExpenseItem
          key={expense.id}
          currentExpense={expense} // 전체 expense 객체 전달
          onDelete={() => handleDelete(expense.id)} // 삭제 핸들러 전달
        />
      ))}
    </ul>
  );
};

export default ExpenseList;
