import React, { useContext, useEffect } from 'react';
import ExpenseItem from "./ExpenseItem";
import { AppContext } from "../../context/AppContext";
import { fetchExpenses } from "../../utils/expense-utils"; // 서버와 통신하는 함수 import
import { Expense } from "../../types/types";

const ExpenseList = () => {
  const { expenses, setExpenses } = useContext(AppContext);

  // 컴포넌트가 처음 렌더링될 때 비용 데이터를 가져오는 useEffect
  useEffect(() => {
    loadExpenses();
  }, []);

  // 서버에서 비용 데이터를 가져와 상태에 설정하는 함수
  const loadExpenses = async () => {
    try {
      const expenseList = await fetchExpenses();
      setExpenses(expenseList);
    } catch (err: any) {
      console.log(err.message);
    }
  };

  return (
    <ul className="list-group">
      {expenses.map((expense: Expense) => (
        <ExpenseItem key={expense.id} id={expense.id} name={expense.name} cost={expense.cost} />
      ))}
    </ul>
  );
};

export default ExpenseList;
