import React, { useContext, useEffect } from "react";
import ExpenseItem from "./ExpenseItem";
import { AppContext } from "../../context/AppContext";
import { fetchExpenses, deleteExpense } from "../../utils/expense-utils";
import { Expense } from "../../types/types";

const ExpenseList = () => {
  const { expenses, setExpenses } = useContext(AppContext);

  useEffect(() => {
    loadExpenses();
  }, []);

  const loadExpenses = async () => {
    try {
      const expenseList = await fetchExpenses();
      setExpenses(expenseList); // 상태 업데이트
    } catch (err: any) {
      console.log(err.message);
    }
  };

  const handleDelete = async (id: string) => {
    try {
      await deleteExpense(id); // 서버에 DELETE 요청
      setExpenses(expenses.filter((expense) => expense.id !== id)); // 상태 업데이트
    } catch (err: any) {
      console.log("Error deleting expense:", err.message);
    }
  };

  if (!expenses || expenses.length === 0) {
    return <p>No expenses available</p>; // 빈 배열일 때 표시할 내용
  }

  return (
    <ul className="list-group">
      {expenses.map((expense: Expense) => (
        <ExpenseItem
          key={expense.id}
          id={expense.id}
          name={expense.name} // 이름 전달
          cost={expense.cost} // 비용 전달
          onDelete={handleDelete} // 삭제 함수 전달
        />
      ))}
    </ul>
  );
};

export default ExpenseList;
